import React from 'react';

const proyectos = [
  {
    id: 1,
    titulo: 'Colecciones - Nuevas llegadas',
    imagen: 'https://cdn.ferrari.com/images/collections/new-arrivals.jpg', // reemplaza con tu URL o local
    enlace: '#',
  },
  {
    id: 2,
    titulo: 'Magazine - Velocidad y belleza',
    imagen: 'https://cdn.ferrari.com/images/magazine/speed-beauty.jpg', // reemplaza con tu URL o local
    enlace: '#',
  },
  {
    id: 3,
    titulo: 'Carreras',
    imagen: 'https://cdn.ferrari.com/images/racing/cars.jpg', // reemplaza con tu URL o local
    enlace: '#',
  },
  {
    id: 4,
    titulo: 'Experiencias',
    imagen: 'https://cdn.ferrari.com/images/experiences/events.jpg', // reemplaza con tu URL o local
    enlace: '#',
  },
];

function Proyectos() {
  return (
    <section
      id="proyectos"
      style={{
        padding: '0',
        backgroundColor: '#111',
        color: '#fff',
        minHeight: '100vh',
      }}
    >
      <h2 style={{ textAlign: 'center', margin: '3rem 0', fontSize: '2.5rem', fontWeight: 'bold' }}>
        Proyectos Destacados
      </h2>

      <div
        style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(2, 1fr)',
          gap: '0',
          maxWidth: '1200px',
          margin: '0 auto',
          height: 'calc(100vh - 120px)', // altura total menos título y márgenes
        }}
      >
        {proyectos.map(({ id, titulo, imagen, enlace }) => (
          <a
            href={enlace}
            key={id}
            style={{
              position: 'relative',
              display: 'block',
              color: '#fff',
              textDecoration: 'none',
              overflow: 'hidden',
            }}
            target="_blank"
            rel="noopener noreferrer"
          >
            <img
              src={imagen}
              alt={titulo}
              style={{
                width: '100%',
                height: '100%',
                objectFit: 'cover',
                transition: 'transform 0.5s ease',
              }}
              className="img-proyecto"
            />
            <div
              style={{
                position: 'absolute',
                top: 0,
                left: 0,
                width: '100%',
                height: '100%',
                display: 'flex',
                flexDirection: 'column',
                justifyContent: 'center',
                alignItems: 'center',
                backgroundColor: 'rgba(0,0,0,0.4)',
                opacity: 0,
                transition: 'opacity 0.4s ease',
                color: '#fff',
                textAlign: 'center',
                padding: '1rem',
              }}
              className="overlay"
            >
              <p style={{ fontSize: '1rem', letterSpacing: '3px', marginBottom: '0.5rem' }}>
                {titulo.includes(' - ') ? titulo.split(' - ')[0].toUpperCase() : 'COLECCIONES'}
              </p>
              <h3 style={{ fontSize: '2rem', fontWeight: 'bold' }}>
                {titulo.includes(' - ') ? titulo.split(' - ')[1].toUpperCase() : titulo.toUpperCase()}
              </h3>
              <button
                style={{
                  marginTop: '1.5rem',
                  border: '1.5px solid white',
                  backgroundColor: 'transparent',
                  color: 'white',
                  padding: '0.5rem 1.5rem',
                  fontWeight: 'bold',
                  cursor: 'pointer',
                  letterSpacing: '2px',
                  textTransform: 'uppercase',
                  transition: 'background-color 0.3s ease',
                }}
                onMouseEnter={(e) => {
                  e.currentTarget.style.backgroundColor = 'white';
                  e.currentTarget.style.color = '#111';
                }}
                onMouseLeave={(e) => {
                  e.currentTarget.style.backgroundColor = 'transparent';
                  e.currentTarget.style.color = 'white';
                }}
              >
                Descubrir
              </button>
            </div>
          </a>
        ))}
      </div>

      <style>{`
        a:hover .img-proyecto {
          transform: scale(1.1);
        }
        a:hover .overlay {
          opacity: 1;
        }
      `}</style>
    </section>
  );
}

export default Proyectos;
