import React from 'react';

const proyectos = [
  {
    id: 1,
    titulo: 'Colecciones - Nuevas llegadas',
    imagen: 'https://cdn.ferrari.com/images/collections/new-arrivals.jpg', // reemplaza con tu URL o local
    enlace: '#',
  },
  {
    id: 2,
    titulo: 'Magazine - Velocidad y belleza',
    imagen: 'https://cdn.ferrari.com/images/magazine/speed-beauty.jpg', // reemplaza con tu URL o local
    enlace: '#',
  },
  {
    id: 3,
    titulo: 'Carreras',
    imagen: 'https://cdn.ferrari.com/images/racing/cars.jpg', // reemplaza con tu URL o local
    enlace: '#',
  },
  {
    id: 4,
    titulo: 'Experiencias',
    imagen: 'https://cdn.ferrari.com/images/experiences/events.jpg', // reemplaza con tu URL o local
    enlace: '#',
  },
];

function Proyectos() {
  return (
    <section
      id="proyectos"
      style={{
        padding: '2rem',
        backgroundColor: '#111',
        color: '#fff',
        minHeight: '100vh',
      }}
    >
      <h2 style={{ textAlign: 'center', marginBottom: '2rem', fontSize: '2rem' }}>
        Proyectos Destacados
      </h2>
      <div
        style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(280px, 1fr))',
          gap: '1.5rem',
          maxWidth: '1200px',
          margin: '0 auto',
        }}
      >
        {proyectos.map(({ id, titulo, imagen, enlace }) => (
          <a
            href={enlace}
            key={id}
            style={{
              position: 'relative',
              display: 'block',
              color: '#fff',
              textDecoration: 'none',
              borderRadius: '10px',
              overflow: 'hidden',
              cursor: 'pointer',
              boxShadow: '0 4px 8px rgba(0,0,0,0.7)',
              transition: 'transform 0.3s ease',
            }}
            target="_blank"
            rel="noopener noreferrer"
          >
            <img
              src={imagen}
              alt={titulo}
              style={{ width: '100%', height: '200px', objectFit: 'cover' }}
            />
            <div
              style={{
                position: 'absolute',
                bottom: '20px',
                left: '20px',
                fontWeight: 'bold',
                fontSize: '1.25rem',
                textShadow: '2px 2px 8px rgba(0,0,0,0.8)',
              }}
            >
              {titulo}
            </div>
            <div
              style={{
                position: 'absolute',
                bottom: '20px',
                right: '20px',
                fontWeight: 'normal',
                fontSize: '1rem',
                padding: '8px 12px',
                border: '2px solid #fff',
                borderRadius: '20px',
                textTransform: 'uppercase',
                transition: 'background-color 0.3s ease, color 0.3s ease',
              }}
              className="btn-descubrir"
            >
              Descubrir â†’
            </div>
          </a>
        ))}
      </div>
    </section>
  );
}

export default Proyectos;
