import React, { useState } from 'react';

const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleMenu = () => setIsOpen(!isOpen);

  return (
    <nav
      style={{
        position: 'fixed',
        top: 0,
        left: 0,
        width: '100%',
        backgroundColor: 'rgba(0,0,0,0.85)',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        padding: '0 2rem',
        height: '60px',
        fontFamily: "'Montserrat', sans-serif",
        color: '#fff',
        boxShadow: '0 2px 4px rgba(0,0,0,0.5)',
        zIndex: 1000,
        flexWrap: 'wrap',
      }}
    >
      <div style={{ fontWeight: 'bold', fontSize: '1.5rem' }}>
        Samu<span style={{ color: '#e10600' }}>.</span>
      </div>

      {/* Botón hamburguesa visible siempre en móvil */}
      <div
        onClick={toggleMenu}
        style={{
          display: 'flex',
          flexDirection: 'column',
          cursor: 'pointer',
          width: '25px',
          height: '20px',
          justifyContent: 'space-between',
          // Ocultar en desktop (>=768px)
          '@media (min-width: 768px)': {
            display: 'none',
          },
        }}
      >
        <div
          style={{
            height: '3px',
            width: '100%',
            backgroundColor: '#fff',
            borderRadius: '10px',
            transition: 'all 0.3s ease',
            transform: isOpen ? 'rotate(45deg) translate(5px, 5px)' : 'none',
          }}
        />
        <div
          style={{
            height: '3px',
            width: '100%',
            backgroundColor: '#fff',
            borderRadius: '10px',
            transition: 'all 0.3s ease',
            opacity: isOpen ? 0 : 1,
          }}
        />
        <div
          style={{
            height: '3px',
            width: '100%',
            backgroundColor: '#fff',
            borderRadius: '10px',
            transition: 'all 0.3s ease',
            transform: isOpen ? 'rotate(-45deg) translate(6px, -6px)' : 'none',
          }}
        />
      </div>

      {/* Menú de links, visible en desktop y toggle en móvil */}
      <div
        style={{
          display: 'flex',
          flexDirection: 'row',
          overflow: 'hidden',
          transition: 'max-height 0.3s ease, padding 0.3s ease',
          maxHeight: isOpen ? '200px' : '0',
          padding: isOpen ? '10px 0' : '0',
          width: '100%',
          justifyContent: 'flex-end',
          gap: '2rem',
          // Mostrar siempre en desktop (>=768px)
          '@media (min-width: 768px)': {
            maxHeight: 'none',
            padding: '0',
            width: 'auto',
          },
        }}
      >
        <a
          href="#presentacion"
          style={linkStyle}
          onClick={() => setIsOpen(false)}
        >
          Inicio
        </a>
        <a
          href="#proyectos"
          style={linkStyle}
          onClick={() => setIsOpen(false)}
        >
          Proyectos
        </a>
        <a
          href="#habilidades"
          style={linkStyle}
          onClick={() => setIsOpen(false)}
        >
          Habilidades
        </a>
      </div>
    </nav>
  );
};

const linkStyle = {
  color: '#fff',
  textDecoration: 'none',
  fontWeight: '500',
  fontSize: '1rem',
  cursor: 'pointer',
  whiteSpace: 'nowrap',
  minWidth: '80px',
  lineHeight: '60px',
};

export default Navbar;
