import React, { useState, useEffect } from 'react';
import AOS from 'aos';
import 'aos/dist/aos.css';

import LoadingScreen from './components/LoadingScreen';
import LandingPage from './pages/LandingPage';
import Proyectos from './pages/Proyectos';
import Habilidades from './pages/Habilidades';

function App() {
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const timer = setTimeout(() => {
      setLoading(false);
    }, 1000); // o ajusta el tiempo seg√∫n tu necesidad

    return () => clearTimeout(timer);
  }, []);

  // üî• este useEffect se ejecuta despu√©s de que loading se vuelve false
  useEffect(() => {
    if (!loading) {
      AOS.init({
        duration: 1000,
        easing: 'ease-in-out',
        once: true,
      });
      AOS.refresh(); // asegura que detecte los elementos cargados despu√©s
    }
  }, [loading]);

  return (
    <div
      style={{
        scrollBehavior: 'smooth',
        minHeight: '100vh',
        backgroundColor: loading ? '#0f172a' : '#ffffff',
      }}
    >
      {loading ? (
        <LoadingScreen />
      ) : (
        <div style={{ paddingTop: '5px' }}>
          <LandingPage />
          <Proyectos />
          <Habilidades />
        </div>
      )}
    </div>
  );
}

export default App;
