import React, { useState } from 'react';

const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleMenu = () => setIsOpen(!isOpen);

  return (
    <nav style={styles.navbar}>
      <div style={styles.logo}>
        Samu<span style={{ color: '#e10600' }}>.</span>
      </div>

      {/* Botón hamburguesa */}
      <div style={styles.hamburger} onClick={toggleMenu}>
        <div style={{ ...styles.bar, transform: isOpen ? 'rotate(45deg) translate(5px, 5px)' : 'none' }} />
        <div style={{ ...styles.bar, opacity: isOpen ? 0 : 1 }} />
        <div style={{ ...styles.bar, transform: isOpen ? 'rotate(-45deg) translate(6px, -6px)' : 'none' }} />
      </div>

      {/* Menú de enlaces */}
      <div
        style={{
          ...styles.linksContainer,
          maxHeight: isOpen ? '200px' : '0',
          padding: isOpen ? '10px 0' : '0',
        }}
      >
        <a href="#presentacion" style={styles.link} onClick={() => setIsOpen(false)}>Inicio</a>
        <a href="#proyectos" style={styles.link} onClick={() => setIsOpen(false)}>Proyectos</a>
        <a href="#habilidades" style={styles.link} onClick={() => setIsOpen(false)}>Habilidades</a>
      </div>
    </nav>
  );
};

const styles = {
  navbar: {
    position: 'fixed',
    top: 0,
    left: 0,
    width: '100%',
    backgroundColor: 'rgba(0,0,0,0.85)',
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: '0 2rem',
    height: '60px',
    fontFamily: "'Montserrat', sans-serif",
    color: '#fff',
    boxShadow: '0 2px 4px rgba(0,0,0,0.5)',
    zIndex: 1000,
    flexWrap: 'wrap',
  },
  logo: {
    fontWeight: 'bold',
    fontSize: '1.5rem',
  },
  hamburger: {
    display: 'none',
    flexDirection: 'column',
    cursor: 'pointer',
    width: '25px',
    height: '20px',
    justifyContent: 'space-between',
  },
  bar: {
    height: '3px',
    width: '100%',
    backgroundColor: '#fff',
    borderRadius: '10px',
    transition: 'all 0.3s ease',
  },
  linksContainer: {
    display: 'flex',
    flexDirection: 'row',
    overflow: 'hidden',
    transition: 'max-height 0.3s ease, padding 0.3s ease',
  },
  link: {
    color: '#fff',
    textDecoration: 'none',
    fontWeight: '500',
    fontSize: '1rem',
    cursor: 'pointer',
    whiteSpace: 'nowrap',
    minWidth: '80px',
    padding: '0 1rem',
    lineHeight: '60px',
  },
};

// Media query con CSS-in-JS: para hacer responsive hay que usar CSS tradicional o librerías
// Aquí un pequeño truco para mostrar el hamburger en pantallas pequeñas

// Se recomienda agregar este CSS en un archivo index.css o usar styled-components o similar

export default Navbar;
