import React from 'react';

const Navbar = () => {
  return (
    <nav style={{
      position: 'fixed',
      top: 0,
      left: 0,
      width: '100%',
      height: '60px',
      backgroundColor: 'rgba(0,0,0,0.85)', // negro semi-transparente
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      padding: '0 2rem',
      zIndex: 1000,
      fontFamily: "'Montserrat', sans-serif",
      color: '#fff',
      boxShadow: '0 2px 4px rgba(0,0,0,0.5)'
    }}>
      <div style={{ fontWeight: 'bold', fontSize: '1.5rem' }}>
        Samu<span style={{ color: '#e10600' }}>.</span>
      </div>
      <div style={{ display: 'flex', gap: '2rem' }}>
        <a href="#presentacion" style={linkStyle}>Inicio</a>
        <a href="#proyectos" style={linkStyle}>Proyectos</a>
        <a href="#habilidades" style={linkStyle}>Habilidades</a>
      </div>
    </nav>
  );
};

const linkStyle = {
    color: '#fff',
    textDecoration: 'none',
    fontWeight: '500',
    fontSize: '1rem',
    cursor: 'pointer',
    transition: 'color 0.3s ease',
    whiteSpace: 'nowrap',       // Evita que el texto se parta en varias líneas
    minWidth: '80px',           // Da un mínimo ancho para que se vea completo
  };

export default Navbar;
