import React, { useEffect, useState } from 'react';

function Ascensor() {
  const [position, setPosition] = useState(0);

  useEffect(() => {
    const handleScroll = () => {
      const scrollY = window.scrollY;
      const windowHeight = window.innerHeight;
      const documentHeight = document.body.scrollHeight;

      // Calcular cuánto % del scroll se ha recorrido
      const scrollPercent = scrollY / (documentHeight - windowHeight);

      // Multiplicar por el alto de la ventana para ubicar la nave
      const newTop = scrollPercent * (windowHeight - 60); // 60 = alto de la nave
      setPosition(newTop);
    };

    window.addEventListener('scroll', handleScroll);
    handleScroll(); // para que arranque bien desde el principio

    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <div
      className="ascensor"
      style={{
        position: 'fixed',
        left: '20px',
        top: `${position}px`,
        zIndex: 999,
        transition: 'top 0.1s ease-out'
      }}
    >
      <img src="/nave.png" alt="Ascensor" style={{ width: '50px' }} />
    </div>
  );
}

export default Ascensor;
